{
    "operationName": "getProcessDefinitionNodes",
    "variables": {
        "processId": "{{process-definition-id}}"
    },
    "query": "query getProcessDefinitionNodes($processId: String) {
  ProcessDefinitions(where: {id: {equal: $processId}}) {
    nodes {
      id
      name
      type
      __typename
    }
    __typename
  }
}
"
}

{
    "operationName": "getTasksForUser",
    "variables": {
        "whereArgument": {
            "and": [
                {},
                {
                    "and": [
                        {
                            "state": {
                                "in": [
                                    "Ready",
                                    "Reserved"
                                ]
                            }
                        }
                    ]
                }
            ]
        },
        "offset": 0,
        "limit": 10,
        "orderBy": {
            "lastUpdate": "DESC"
        }
    },
    "query": "query getTasksForUser($whereArgument: UserTaskInstanceArgument, $offset: Int, $limit: Int, $orderBy: UserTaskInstanceOrderBy) {
  UserTaskInstances(where: $whereArgument, pagination: {offset: $offset, limit: $limit}, orderBy: $orderBy) {
    id
    name
    referenceName
    description
    priority
    processInstanceId
    processId
    rootProcessInstanceId
    rootProcessId
    state
    actualOwner
    adminGroups
    adminUsers
    completed
    started
    excludedUsers
    potentialGroups
    potentialUsers
    inputs
    outputs
    lastUpdate
    endpoint
    externalReferenceId
    __typename
  }
}
"
}

{
    "operationName": "getProcessInstances",
    "variables": {
        "where": {
            "parentProcessInstanceId": {
                "isNull": true
            },
            "state": {
                "in": [
                    "ACTIVE"
                ]
            }
        },
        "offset": 0,
        "limit": 10,
        "orderBy": {
            "lastUpdate": "DESC"
        }
    },
    "query": "query getProcessInstances($where: ProcessInstanceArgument, $offset: Int, $limit: Int, $orderBy: ProcessInstanceOrderBy) {
  ProcessInstances(where: $where, pagination: {offset: $offset, limit: $limit}, orderBy: $orderBy) {
    id
    processId
  version
  processName
    parentProcessInstanceId
    rootProcessInstanceId
    roles
    state
    start
    lastUpdate
    addons
    businessKey
    serviceUrl
    error {
      nodeDefinitionId
      message
      __typename
    }
    __typename
  }
}
"
}

{
    "operationName": "getProcessInstanceById",
    "variables": {
        "id": "{{process-instance-id}}"
    },
    "query": "query getProcessInstanceById($id: String) {
  ProcessInstances(where: {id: {equal: $id}}) {
    id
    processId
    processName
    businessKey
    parentProcessInstanceId
    parentProcessInstance {
      id
      processName
      businessKey
      __typename
    }
    roles
    variables
    state
    start
    lastUpdate
    end
     endpoint
    addons
    serviceUrl
       error {
      nodeDefinitionId
      message
      __typename
    }
    childProcessInstances {
      id
      processName
      businessKey
      __typename
    }
    nodes {
      id
      nodeId
      name
      enter
      exit
      type
      definitionId
      __typename
    }
    milestones {
      id
      name
      status
      __typename
    }
    __typename
  }
}
"
}

{
    "query": "query { ProcessInstances ( where: {   id: {  equal : \"{{process-instance-id}}\" } }) { nodes { definitionId exit } } }"
}

{
    "operationName": "getJobsWithFilters",
    "variables": {
        "values": [
            "SCHEDULED"
        ],
        "offset": 0,
        "limit": 10,
        "orderBy": {
            "lastUpdate": "DESC"
        }
    },
    "query": "query getJobsWithFilters($values: [JobStatus], $orderBy: JobOrderBy, $offset: Int, $limit: Int) {
  Jobs(where: {status: {in: $values}}, orderBy: $orderBy, pagination: {offset: $offset, limit: $limit}) {
    id
    processId
    processInstanceId
    rootProcessId
    status
    expirationTime
    priority
    callbackEndpoint
    repeatInterval
    repeatLimit
    scheduledId
    retries
    lastUpdate
    endpoint
    executionCounter
    __typename
  }
}
"
}

